function initnavbar(){document.querySelectorAll(".navbar-toggler").forEach((e=>{e.addEventListener("click",(o=>open_navbar(e.dataset.target)))}))}function initmodal(){document.querySelectorAll(".btn,.close,.modal").forEach((e=>{e.classList.contains("modal")&&e.addEventListener("click",(o=>{o.target==e&&close_modal()})),"modal"==e.dataset.toggle&&e.addEventListener("click",(o=>open_modal(e.dataset.target))),"modal"==e.dataset.dismiss&&e.addEventListener("click",close_modal)}))}function open_modal(e){document.querySelectorAll(e).forEach((e=>{e.classList.add("showing")}))}function close_modal(){document.querySelectorAll(".modal").forEach((e=>{e.classList.contains("showing")&&(e.classList.add("goingout"),e.addEventListener("animationend",(()=>{e.classList.contains("goingout")&&(e.classList.remove("goingout"),e.classList.remove("showing"))})))}))}function open_navbar(e){document.querySelectorAll(e).forEach((e=>{e.classList.contains("opened")?e.classList.remove("opened"):e.classList.add("opened")}))}window.onload=()=>{initnavbar(),initmodal()};
